descriptor.controllerScript:
self.onInit = function() {

    self.ctx.$scope.deviceTitle = self.ctx.settings
        .deviceTitle;
    self.ctx.$scope.deviceSubtitle = self.ctx.settings
        .deviceSubtitle;
    self.ctx.$scope.imageName = self.ctx.settings
        .imageName;
    self.ctx.$scope.rpcMethod = self.ctx.settings
        .rpcMethod;
    self.ctx.$scope.rpcType = self.ctx.settings
        .rpcType;
    self.ctx.$scope.timeout = self.ctx.settings
        .requestTimeout;

    self.ctx.$scope.statusDevice = false;

    self.ctx.$scope.wifiStatus =
        "wiki: 1231 /n Ip: 123123";

    if (!self.ctx.$scope.deviceTitle) {
        self.ctx.$scope.deviceTitle = "Device Title";
    }
    if (!self.ctx.$scope.deviceSubtitle) {
        self.ctx.$scope.deviceSubtitle =
            "Device Subtitle";
    }
    if (!self.ctx.$scope.imageName) {
        self.ctx.$scope.imageName = "imageName";
    }
    if (!self.ctx.$scope.rpcMethod) {
        self.ctx.$scope.rpcMethod = "RPC_SWITCH_METHOD";
    }
    if (!self.ctx.$scope.rpcType) {
        self.ctx.$scope.rpcType = "oneway";
    }
    if (!self.ctx.$scope.timeout) {
        self.ctx.$scope.timeout = 1000;
    }


    self.ctx.$scope.sendCommand = function(status) {
        console.log("status", status)
        console.log('rpcMethod', self.ctx.$scope
            .rpcMethod)
        console.log('timeout', self.ctx.$scope
            .timeout)
        var rpcParams = {
            switch: status,
            value: "0"
        };

        var oneWayElseTwoWay = false

        if (self.ctx.$scope.rpcType === "oneway") {
            oneWayElseTwoWay = true;
        } else {
            oneWayElseTwoWay = false;
        }

        var commandObservable;
        if (oneWayElseTwoWay) {
            console.log("oneway")
            console.log(JSON.stringify(rpcParams))
            commandObservable = self.ctx.controlApi
                .sendOneWayCommand(self.ctx.$scope
                    .rpcMethod, rpcParams, self.ctx
                    .$scope.timeout);
        } else {
            console.log(rpcParams)
            console.log("twoway")
            console.log(JSON.parse(rpcParams))
            commandObservable = self.ctx.controlApi
                .sendTwoWayCommand(self.ctx.$scope
                    .rpcMethod, JSON
                    .parse(rpcParams), self.ctx
                    .$scope.timeout);
        }
        commandObservable.subscribe(
            function(response) {
                if (oneWayElseTwoWay) {
                    self.ctx.$scope
                        .rpcCommandResponse =
                        "Command was successfully received by device.<br> No response body because of one way command mode.";
                } else {
                    self.ctx.$scope
                        .rpcCommandResponse =
                        "Response from device:<br>";
                    self.ctx.$scope
                        .rpcCommandResponse +=
                        JSON.stringify(response,
                            undefined, 2);
                }
                self.ctx.detectChanges();
            },
            function(rejection) {
                self.ctx.$scope
                    .rpcCommandResponse =
                    "Failed to send command to the device:<br>"
                self.ctx.$scope
                    .rpcCommandResponse +=
                    "Status: " + rejection
                    .status + "<br>";
                self.ctx.$scope
                    .rpcCommandResponse +=
                    "Status text: '" + rejection
                    .statusText + "'";
                self.ctx.detectChanges();
            }

        );
    }

    self.ctx.$scope.gpioToggleChange = function(
        $event) {
        self.ctx.$scope.sendCommand($event.target
            .checked)
    };

    const datasources = [{
        type: "entity",
        dataKeys: [{
            decimals: 0,
            label: "",
            name: "connectivity",
            settings: {},
            type: "attribute"
        }],
        entityFilter: {
            type: "singleEntity",
            singleEntity: {
                id: "cde78cc0-2398-11ef-b19f-cdc6a3f105ff",
                entityType: "DEVICE"
            }
        }
    }];

    const subscriptionOptions = {
        type: 'latest',
        datasources: datasources,
        callbacks: {
            onDataUpdated: () => {
                self.onDataUpdated()
            }
        }
    };

    self.ctx.subscriptionApi.createSubscription(
        subscriptionOptions, true).subscribe(
        (subscription) => {
            self.ctx.defaultSubscription = subscription; //Saving subscription information into widget context
            self.ctx.data = subscription.data; //Saving data into widget context
            self.ctx.datasources = subscription.datasources; //Saving datasource into widget context
        }
    );
    
    self.ctx.$scope.data = self.ctx.data;

}

self.onDataUpdated = function() {
    console.log('onDataUpdated')
    console.log('data', self.ctx.data)
    self.ctx.$scope.statusDevice = JSON.parse(self.ctx.data[0].data[0][1]);
    self.ctx.detectChanges();
}

