descriptor.controllerScript:
self.onInit = function() {
    
    self.ctx.$scope.data = self.ctx.defaultSubscription.data;
    self.ctx.$scope.deviceTitle = self.ctx.settings.deviceTitle;
    self.ctx.$scope.deviceSubtitle = self.ctx.settings.deviceSubtitle;
    self.ctx.$scope.imageName = self.ctx.settings.imageName;
    self.ctx.$scope.deviceName = self.ctx.settings.deviceName;
    self.ctx.$scope.entityId = self.ctx.settings.entityId;
    self.ctx.$scope.deviceStatus1 = false;
    self.ctx.$scope.deviceStatus2 = false;
    self.ctx.$scope.deviceStatus3 = false;
    self.ctx.$scope.deviceStatus4 = false;
    self.ctx.$scope.rpcMethod = self.ctx.settings.rpcMethod;
    self.ctx.$scope.rpcType = self.ctx.settings.rpcType;
    self.ctx.$scope.timeout = self.ctx.settings.requestTimeout;
    self.ctx.$scope.statusDevice = false;
    self.ctx.$scope.ssid = false;
    self.ctx.$scope.rssi = false;
    self.ctx.$scope.checkState = false;
    self.ctx.$scope.wifiStatus =`ssid ${self.ctx.$scope.ssid} rssi ${self.ctx.$scope.rssi}`;

    if (!self.ctx.$scope.deviceTitle) {self.ctx.$scope.deviceTitle = "Device Title"}
    if (!self.ctx.$scope.deviceSubtitle) {self.ctx.$scope.deviceSubtitle ="Device Subtitle"}
    if (!self.ctx.$scope.imageName) {self.ctx.$scope.imageName = "/api/images/public/4dkyQ3xhwWMgDUYcZSI71QNxndRSvaRt"}
    if (!self.ctx.$scope.entityId) {self.ctx.$scope.entityId = "cde78cc0-2398-11ef-b19f-cdc6a3f105ff"}
    if (!self.ctx.$scope.deviceName) {self.ctx.$scope.deviceName = "device1"}
    if (!self.ctx.$scope.rpcMethod) {self.ctx.$scope.rpcMethod = "set_switch"}
    if (!self.ctx.$scope.rpcType) {self.ctx.$scope.rpcType = "oneway"}
    if (!self.ctx.$scope.timeout) {self.ctx.$scope.timeout = 1000}

    self.ctx.$scope.sendCommand = function(status) {
        
        console.log('rpcMethod', self.ctx.$scope.rpcMethod);
        console.log('timeout', self.ctx.$scope.timeout);
        var rpcParams = {
            switch: status,
            value: "0"
        };

        var oneWayElseTwoWay = false;

        if (self.ctx.$scope.rpcType === "oneway") {
            oneWayElseTwoWay = true;
        } else {
            oneWayElseTwoWay = false;
        }

        var commandObservable;
        if (oneWayElseTwoWay) {
            console.log("oneway");
            console.log(JSON.stringify(rpcParams));
            commandObservable = self.ctx.controlApi
                .sendOneWayCommand(self.ctx.$scope
                    .rpcMethod, rpcParams, self.ctx
                    .$scope.timeout);
        } else {
            console.log(rpcParams);
            console.log("twoway");
            console.log(JSON.parse(rpcParams));
            commandObservable = self.ctx.controlApi
                .sendTwoWayCommand(self.ctx.$scope
                    .rpcMethod, JSON
                    .parse(rpcParams), self.ctx
                    .$scope.timeout);
        }
        commandObservable.subscribe(
            function(response) {
                if (oneWayElseTwoWay) {
                    self.ctx.$scope
                        .rpcCommandResponse =
                        "Command was successfully received by device.<br> No response body because of one way command mode.";
                } else {
                    self.ctx.$scope
                        .rpcCommandResponse =
                        "Response from device:<br>";
                    self.ctx.$scope
                        .rpcCommandResponse +=
                        JSON.stringify(response,
                            undefined, 2);
                }
                self.ctx.detectChanges();
            },
            function(rejection) {
                self.ctx.$scope
                    .rpcCommandResponse =
                    "Failed to send command to the device:<br>";
                self.ctx.$scope
                    .rpcCommandResponse +=
                    "Status: " + rejection
                    .status + "<br>";
                self.ctx.$scope
                    .rpcCommandResponse +=
                    "Status text: '" + rejection
                    .statusText + "'";
                self.ctx.detectChanges();
            }
        );
    };

    self.ctx.$scope.gpioToggleChange = function(
        $event) {
        // self.ctx.$scope.sendCommand($event.target
        //     .checked);
        
        // Mapa de dispositivos y sus claves de estado
            const deviceStateMap = {
                'device1': 'deviceState1',
                'device2': 'deviceState2',
                'device3': 'deviceState3',
                'device4': 'deviceState4'
            };
            
            // Obtiene el nombre del dispositivo actual
            const deviceName = self.ctx.$scope.deviceName;
            
            // Verifica si el nombre del dispositivo está en el mapa
            if (deviceStateMap.hasOwnProperty(deviceName)) {
                const stateKey = deviceStateMap[deviceName];
            
                self.ctx.attributeService.saveEntityAttributes(
                    { entityType: 'DEVICE', id: self.ctx.$scope.entityId },
                    'SHARED_SCOPE', [{
                        key: stateKey,
                        value: $event.target.checked
                    }]
                ).subscribe((res) => {
                    console.log(res);
                });
            }
        
    };

    const datasources = [{
        type: "entity",
        dataKeys: [{
            decimals: 0,
            label: "",
            name: "connectivity",
            settings: {},
            type: "attribute"
        },{
            decimals: 0,
            label: "",
            name: "ssid",
            settings: {},
            type: "attribute"
        },{
            decimals: 0,
            label: "",
            name: "rssi",
            settings: {},
            type: "attribute"
        },{
            decimals: 0,
            label: "",
            name: "deviceState1",
            settings: {},
            type: "attribute"
        },{
            decimals: 0,
            label: "",
            name: "deviceState2",
            settings: {},
            type: "attribute"
        },{
            decimals: 0,
            label: "",
            name: "deviceState3",
            settings: {},
            type: "attribute"
        },{
            decimals: 0,
            label: "",
            name: "deviceState4",
            settings: {},
            type: "attribute"
        }],
        entityFilter: {
            type: "singleEntity",
            singleEntity: {
                id: self.ctx.$scope.entityId,
                entityType: "DEVICE"
            }
        }
    }];

    const subscriptionOptions = {
        type: 'latest',
        datasources: datasources,
        callbacks: {
            onDataUpdated: () => {
                self.onDataUpdated();
            }
        }
    };

    self.ctx.subscriptionApi.createSubscription(
        subscriptionOptions, true).subscribe(
        (subscription) => {
            self.ctx.defaultSubscription = subscription;
            self.ctx.data = subscription.data;
            self.ctx.datasources = subscription.datasources;
        }
    );
    
    self.ctx.$scope.data = self.ctx.data;
};

self.onDataUpdated = function() {
    // console.log('onDataUpdated');
    // console.log('data', self.ctx.data);

    try {
        const statusDevice = JSON.parse(self.ctx.data[0].data[0][1]);
        self.ctx.$scope.statusDevice = statusDevice;
    } catch (e) {
        console.error('Error parsing statusDevice', e);
    }

    try {
        const ssid = self.ctx.data[1].data[0][1];
        self.ctx.$scope.ssid = ssid;
    } catch (e) {
        console.error('Error parsing ssid', e);
    }

    try {
        const rssi = JSON.parse(self.ctx.data[2].data[0][1]);
        self.ctx.$scope.rssi = rssi;
    } catch (e) {
        console.error('Error parsing rssi', e);
    }

    try {
        const device1 = JSON.parse(self.ctx.data[3].data[0][1]);
        self.ctx.$scope.deviceStatus1 = device1;
    } catch (e) {
        console.error('Error parsing device1', e);
    }
    
    try {
        const device2 = JSON.parse(self.ctx.data[4].data[0][1]);
        self.ctx.$scope.deviceStatus2 = device2;
    } catch (e) {
        console.error('Error parsing device2', e);
    }
    
    try {
        const device3 = JSON.parse(self.ctx.data[5].data[0][1]);
        self.ctx.$scope.deviceStatus3 = device3;
    } catch (e) {
        console.error('Error parsing device3', e);
    }
    
    try {
        const device4 = JSON.parse(self.ctx.data[6].data[0][1]);
        self.ctx.$scope.deviceStatus4 = device4;
    } catch (e) {
        console.error('Error parsing device4', e);
    }
    
    if (self.ctx.$scope.deviceName === 'device1'){
        console.log('device1')
        self.ctx.$scope.checkState = self.ctx.$scope.deviceStatus1;
    }
    
    if (self.ctx.$scope.deviceName === 'device2'){
        console.log('device2')
        self.ctx.$scope.checkState = self.ctx.$scope.deviceStatus2;
    }
    
    if (self.ctx.$scope.deviceName === 'device3'){
        console.log('device3')
        self.ctx.$scope.checkState = self.ctx.$scope.deviceStatus3;
    }
    
    if (self.ctx.$scope.deviceName === 'device4'){
        console.log('device4')
        self.ctx.$scope.checkState = self.ctx.$scope.deviceStatus4;
    };
    
    self.ctx.$scope.wifiStatus =`SSID: ${self.ctx.$scope.ssid} / RSSI: ${self.ctx.$scope.rssi}`;
    
    console.log(self.ctx.$scope.data)
    self.ctx.detectChanges();
};

